<?php/** * Purlems View for Purlem Component *  * @package    Joomla.Tutorials * @subpackage Components * @link http://docs.joomla.org/Developing_a_Model-View-Controller_Component_-_Part_4 * @license		GNU/GPL */// No direct accessdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.view' );/** * Purlems View * * @package    Joomla.Tutorials * @subpackage Components */class PurlemsViewPurlems extends JView{	/**	 * Purlems view display method	 * @return void	 **/	function display($tpl = null)	{		JToolBarHelper::title(   JText::_( 'Purl Manager' ), 'generic.png' );		JToolBarHelper::save();		// Get data from the model		$items		= & $this->get( 'Data');		$this->assignRef('items',		$items);		parent::display($tpl);	}}