<?php/** * Purlem Model for Purlem World Component *  * @package    Joomla.Tutorials * @subpackage Components * @license		GNU/GPL */// Check to ensure this file is included in Joomla!defined('_JEXEC') or die();jimport( 'joomla.application.component.model' );/** * Purlem Model * * @package    Joomla.Tutorials * @subpackage Components */class PurlemModelPurlem extends JModel{	/**	 * Gets the greeting	 * @return string The greeting to be displayed to the user	 */	function getGreeting()	{		$db =& JFactory::getDBO();		$query = 'SELECT * FROM #__purlem WHERE id=1';		$db->setQuery( $query );		$greeting = $db->loadRow();		return $greeting;	}}